function init(){for(var n=document.getElementById("code").innerHTML,e=0,o=[],t="<span class='perc'>"+n[1]+"</span>",d=0;d<=n.length;d++)"%"===n[d]?o.push(t):o.push(n[d]);document.getElementById("code").innerHTML="";for(var c=0;c<=o.length;c++)document.getElementById("code").innerHTML+=o[c];console.log(o)}window.onload=init;